void EMissile(int EMT, int EML)
{
	setPixel(EML,EMT- 4);
	setPixel(EML,EMT- 5);
	//Left Head of enemy missile
	setPixel(EML+ 1,EMT);
	setPixel(EML+ 1,EMT- 1);
	setPixel(EML+ 1,EMT- 2);
	setPixel(EML+ 1,EMT- 3);
	setPixel(EML+ 1,EMT- 4);
	setPixel(EML+ 1,EMT- 5);
	//Middle Row
	setPixel(EML+ 2,EMT- 4);
	setPixel(EML+ 2,EMT- 5);
	//Third Left Row
}
void Missile(int MTop, int ML)
{
	//Missile is 6 tall and 3 wide.

	setPixel(ML,MTop- 1);
	setPixel(ML,MTop- 2);
	//Left Head of Missile/ First Left Row
	setPixel(ML+ 1,MTop);
	setPixel(ML+ 1,MTop- 1);
	setPixel(ML+ 1,MTop- 2);
	setPixel(ML+ 1,MTop- 3);
	setPixel(ML+ 1,MTop- 4);
	setPixel(ML+ 1,MTop- 5);
	//Full length/ Middle Row of Missile/ Second Left Row
	setPixel(ML+ 2,MTop- 1);
	setPixel(ML+ 2,MTop- 2);
	//Right side Of Missile Head/ Third Left Row
}
