task main()
{
	ShipTop = 30;
	ShipL = 30;
	resetGyro(Gyro1);
	resetGyro(Gyro2);
	while(true)
	{
		if (getGyroDegrees(Gyro1) < 45 || getGyroDegrees(Gyro1) > 315) {
   Ship(ShipTop, ShipL);
   Ship(ShipTop + 20, ShipL + 30);
			} else if (getGyroDegrees(Gyro1) < 135 || getGyroDegrees(Gyro1) > 45) {
      Missile( 20,20);
      Missile( 40,40);
			} else if (getGyroDegrees(Gyro1) <225  || getGyroDegrees(Gyro1) > 135) {
     EMissile( 40, 20);
     EMissile( 50, 20);
     EMissile( 30, 20);
     EMissile( 20, 20);
			} else if (getGyroDegrees(Gyro1) <315  || getGyroDegrees(Gyro1) > 225) {
     Missile( 20,20);
     Ship( 40,40);
     EMissile(60,40);
		}


	}
}
